import React, { useState, useEffect } from "react";
import Book from "./Book.js";
import Header from "./Header.js";
import "./App.css";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

// const authorApi =
//   "https://openlibrary.org/search/authors.json?q=j%20k%20rowling";

// const searchApi = `https://openlibrary.org/search.json?title=${}`;

//`https://openlibrary.org/search.json?title=${book}&limit=20&page=${page}`

const App = () => {
  function Home() {
    return <h2>Home</h2>;
  }

  function About() {
    return <h2>About</h2>;
  }

  function Users() {
    return <h2>Users</h2>;
  }

  const [books, getBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchTerm, getSearchTerm] = useState("book");
  const [page, setPage] = useState(1);

  const fetchBooks = async (book) => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(
        `https://openlibrary.org/search.json?title=${book}&limit=20&page=${page}`
      );

      const data = await res.json();
      console.log(res);

      if (!res.ok) throw new Error("Something went wrong");

      console.log(data.docs[0].cover_i);
      const transformedBooks = data.docs.map((bookData) => {
        return {
          title: bookData.title,
          cover_i: bookData.cover_i,
        };
      });
      console.log(transformedBooks);
      getBooks(transformedBooks);
    } catch (error) {
      setError(error.message);
    }
    setLoading(false);
  };
  //   //   use effect
  //   useEffect((searchTerm) => {
  //     setTimeout(fetchBooks(searchTerm), 300);
  //   }, []);

  return (
    <Router>
      <div className="app">
        <Header
          searchTerm={searchTerm}
          getSearchTerm={getSearchTerm}
          fetchBooks={fetchBooks}
        ></Header>
        {loading && <div>Loading...</div>}
        {!loading && books.length > 0 && (
          <div className="container">
            {books.map((book, index) => {
              return (
                <Book
                  key={index}
                  title={book.title}
                  image={`https://covers.openlibrary.org/b/id/${book.cover_i}-M.jpg?default=false`}
                />
              );
            })}
          </div>
        )}
        {!loading && books.length === 0 && !error && <p>No books found</p>}
        {!loading && error && <p>{error}</p>}
      </div>

      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/about">About</Link>
            </li>
            <li>
              <Link to="/users">Users</Link>
            </li>
          </ul>
        </nav>

        {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
        <Switch>
          <Route path="/about">
            <About />
          </Route>
          <Route path="/users">
            <Users />
          </Route>
          <Route path="/">
            <Home />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
