import React from "react";
import Button from "./Button";
import Input from "./Input";
import classes from "./Header.module.css";

const Header = (props) => {
  const [searchString, setSearchString] = useState("");

  const fetchBooks = async (book) => {
    const result = await fetch(
      `https://openlibrary.org/search.json?title=${book}&limit=20`
    );

    const data = await result.json();
    console.log(data);
    console.log("jel radi?");
  };

  return (
    <div className={classes.header}>
      <form>
        <label htmlFor="search">Search Book</label>
        <Input type="text" name="search" placeholder="type here to find book" />
      </form>
      <Button type="submit" onClick={fetchBooks}>
        Find Book
      </Button>
    </div>
  );
};

export default Header;
