import React, { useState, useEffect } from "react";
import Book from "./Book.js";
import "./App.css";
import SearchIcon from "./search.svg";

// const authorApi =
//   "https://openlibrary.org/search/authors.json?q=j%20k%20rowling";

// const searchApi = `https://openlibrary.org/search.json?title=${}`;

const App = () => {
  const [books, getBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [image, getImage] = useState();
  const [title, getTitle] = useState();
  const [searchTerm, getSearchTerm] = useState("tolkien");

  const serachBooks = async (book) => {
    setLoading(true);
    const res = await fetch(
      `https://openlibrary.org/search.json?title=${book}`
    );
    const data = await res.json();

    // getBooks(data.entries);
    console.log(data.docs);
    getBooks(data.docs);
    console.log(data.docs[0].title);
    console.log(data.docs[0].isbn[0]);

    const imgresult = await fetch(
      `https://covers.openlibrary.org/b/isbn/${data.docs[0].isbn[2]}-M.jpg`
    );
    setLoading(false);
    console.log(imgresult.url);
    getImage(imgresult.url);
    getTitle(data.docs[0].title);
  };

  //use effect
  useEffect((searchTerm) => {
    serachBooks(searchTerm);
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }
  if (!loading) {
    return (
      <div className="app">
        <h1>Book Land</h1>
        <div className="search">
          <input
            placeholder="Search for books"
            value={searchTerm}
            onChange={(e) => {
              getSearchTerm(e.target.value);
            }}
          ></input>
          <img
            src={SearchIcon}
            alt="search"
            onClick={() => serachBooks(searchTerm)}
          />
        </div>
        <div className="container">
          {books.forEach((book) => (
            <Book
              title={book.title}
              //   image={`https://covers.openlibrary.org/b/isbn/${book.isbn[0]}-M.jpg`}
            />
          ))}
        </div>
      </div>
    );
  }
};

