import classes from "./Book.module.css";
import React from "react";
import Button from "./Button";
import Modal from "../components/UI/Modal";

const Book = ({ book, onAddToReadedList, onAddToReadingList, onShowModal }) => {
  //destrucrure props
  return (
    <div className={classes.book} key={book.id}>
      <img
        src={
          book.cover
            ? `https://covers.openlibrary.org/b/id/${book.cover}-M.jpg?default=false`
            : "https://blog.springshare.com/wp-content/uploads/2010/02/nc-md.gif"
        }
        alt="no cover found"
      ></img>
      <h4>{book.title}</h4>
      <h5>by: {book.author}</h5>
      {!book.readed ? (
        <Button onClick={() => onAddToReadingList(book)}>
          {!book.currentlyReading ? "Add to reading list" : "Currently reading"}
        </Button>
      ) : (
        <p id="readed">Book readed!!!</p>
      )}
      {!book.readed && (
        <Button onClick={() => onAddToReadedList(book)}>Mark as read</Button>
      )}
      <Button onClick={onShowModal}>info</Button>
      {bookCtx.showModal && <Modal book={book} />}
    </div>
  );
};

export default Book;

