import React, { useState, useEffect } from "react";

// const apiUrl = "https://openlibrary.org/authors/OL23919A/works.json";
// const authorApi =
//   "https://openlibrary.org/search/authors.json?q=j%20k%20rowling";

// http://covers.openlibrary.org/b/isbn/  ovo ti je za slike https://covers.openlibrary.org/b/isbn/842066085X-M.jpg
// const searchApi = `https://openlibrary.org/search.json?title=${}`;

// console.log(data);

const App = () => {
  const [books, getBooks] = useState();
  const [image, getImage] = useState();

  const serachBooks = async (book) => {
    const res = await fetch(
      `https://openlibrary.org/search.json?title=${book}`
    );
    const data = await res.json();

    // getBooks(data.entries);
    console.log(data.docs[0].title);
    console.log(data.docs[0].isbn[2]);

    const imgresult = await fetch(
      `https://covers.openlibrary.org/b/isbn/${data.docs[0].isbn[1]}-M.jpg`
    );

    console.log(imgresult.url);
    getImage(imgresult.url);
  };

  useEffect(() => {
    serachBooks("catcher in");
  }, []);
  return (
    <div className="app">
      <h1>Book Land</h1>
      <div className="search">
        <input
          placeholder="Search for books"
          value=""
          onChange={() => {}}
        ></input>
        <img src="" alt="search" onClick={() => {}} />
      </div>
      <div className="container"></div>
      <img src={image} alt="search" />
    </div>
  );
};

export default App;
