import React, { useState } from "react";
import Button from "./Button";
import Input from "./Input";
import classes from "./Header.module.css";

const Header = (props) => {
  const [searchString, setSearchString] = useState("");

  const searchStringHandler = (e) => {
    setSearchString(e.target.value);

    console.log(searchString);
    console.log("a ovo jel radi");
  };
  const fetchBooks = async (e) => {
    e.preventDefault();
    const result = await fetch(
      `https://openlibrary.org/search.json?title=${searchString}&limit=20`
    );

    const data = await result.json();
    console.log(data);

    const transformedBooks = data.docs.map((book) => {
      return {
        title: book.title,
        author: book.author_name,
        cover: book.cover_i,
      };
    });

    props.getBooks(transformedBooks);
  };

  //   fetchBooks(searchString)

  return (
    <div className={classes.header}>
      <form onSubmit={fetchBooks}>
        <label htmlFor="search">Search Book</label>
        <Input
          type="text"
          name="search"
          placeholder="type here to find book"
          onChange={searchStringHandler}
        />
        <Button type="submit" onClick={fetchBooks}>
          Find Book
        </Button>
      </form>
    </div>
  );
};

export default Header;
