import { Fragment, useContext } from "react";
import ReactDOM from "react-dom";
import Button from "./Button";
import BookContext from "../../store/book-context";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCheckCircle,
  faCircleXmark,
} from "@fortawesome/free-solid-svg-icons";

import classes from "./Modal.module.css";

const Backdrop = (props) => {
  return <div className={classes.backdrop} onClick={props.onClose} />;
};

const ModalOverlay = (props) => {
  return (
    <div className={classes.modal}>
      <div className={classes.content}>{props.children}</div>
    </div>
  );
};
const Content = ({ books, bookCtx }) => {
  return books
    .filter((book) => book.id === bookCtx.idForModal)
    .map((book) => {
      console.log(book);
      return (
        <div className={classes.info}>
          <div>
            <img
              src={
                book.cover
                  ? `https://covers.openlibrary.org/b/id/${book.cover}-M.jpg?default=false`
                  : "https://blog.springshare.com/wp-content/uploads/2010/02/nc-md.gif"
              }
              alt="no cover found"
            ></img>
          </div>
          <div>
            <ul>
              <li>
                <h3>{book.title}</h3>
              </li>
              <li>id: {book.id}</li>
              <li>author: {book.author}</li>

              <li>language: {book.language[0]}</li>

              <li>published: {book.publishDate}</li>
              <li>publisher: {book.publisher}</li>
              <li>
                readed status:
                {book.readed ? (
                  <FontAwesomeIcon
                    icon={faCheckCircle}
                    className={classes.icon}
                  />
                ) : (
                  <FontAwesomeIcon
                    icon={faCircleXmark}
                    className={classes.icon}
                  />
                )}
              </li>
            </ul>
          </div>
        </div>
      );
    });
};

const portalElement = document.getElementById("overlays");

const Modal = ({ books }) => {
  const bookCtx = useContext(BookContext);
  return (
    <Fragment>
      {ReactDOM.createPortal(
        <Backdrop onClose={bookCtx.showModalHandler} />,
        portalElement
      )}
      {ReactDOM.createPortal(
        <ModalOverlay>
          <Content books={books} bookCtx={bookCtx} />
          <Button onClick={bookCtx.showModalHandler}>Close details</Button>
        </ModalOverlay>,
        portalElement
      )}
    </Fragment>
  );
};

export default Modal;
