import classes from "./Book.module.css";
import React from "react";
import Button from "./Button";

const Book = (props) => {
  console.log(props.book);
  return (
    <div className={classes.book}>
      <img
        src={
          props.book.cover
            ? `https://covers.openlibrary.org/b/id/${props.book.cover}-M.jpg?default=false`
            : "https://blog.springshare.com/wp-content/uploads/2010/02/nc-md.gif"
        }
        alt="no cover found"
      ></img>{" "}
      <h4>{props.book.title}</h4>
      <h5>by: {props.book.author}</h5>
      <Button onClick={props.onAddToReadingList}>Add to reading list</Button>
      <Button>Mark as read</Button>
    </div>
  );
};

export default Book;
