import classes from "./Profile.module.css";
import React, { useContext } from "react";
import BookContext from "../store/book-context";
import Book from "./Book";
import { Fragment } from "react";

const Profile = () => {
  const bookCtx = useContext(BookContext);

  // useEffect(() => {
  //   CurrentlyReading();
  //   ReadedBooks();
  // }, [bookCtx.books, bookCtx.readedBooks]);

  const renderBooks = (books) => {
    return books.map((book) => {
      return (
        <div>
          <Book
            book={book}
            key={book.id}
            onAddToReadingList={bookCtx.setBooksToLS}
            onAddToReadedList={bookCtx.addToReadedBooksList}
            onShowModal={bookCtx.showModalHandler}
            getIdForModal={bookCtx.getIdForModal}
          />
        </div>
      );
    });
  };

  const CurrentlyReading = () => {
    return (
      <Fragment>
        <h3>Currnetly Reading Books</h3>
        <div className={classes.readed}>
          {renderBooks(bookCtx.currReadingbooks)}
        </div>
      </Fragment>
    );
  };

  const ReadedBooks = () => {
    return (
      <Fragment>
        <h3>Readed Books</h3>
        <div className={classes.readed}>{renderBooks(bookCtx.readedBooks)}</div>
      </Fragment>
    );
  };

  return (
    <div className={classes.profile}>
      <div className={classes["profile-title"]}>
        <h2>Nemanja Mijucic</h2>
        <p>A reader lives a thousand lives before he dies </p>
      </div>

      <CurrentlyReading />
      <ReadedBooks />
    </div>
  );
};

export default Profile;
