import React, { useState, useEffect } from "react";
import Book from "./Book.js";
import Header from "./Header.js";
import "./App.css";

// const authorApi =
//   "https://openlibrary.org/search/authors.json?q=j%20k%20rowling";

// const searchApi = `https://openlibrary.org/search.json?title=${}`;

const App = () => {
  const [books, getBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchTerm, getSearchTerm] = useState("");

  //   use effect
  useEffect((searchTerm) => {
    fetchBooks(searchTerm);
  }, []);

  const fetchBooks = async (book) => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(
        `https://openlibrary.org/search.json?title=${book}&limit=20`
      );

      const data = await res.json();
      console.log(res);

      if (!res.ok) throw new Error("Something went wrong");

      console.log(data.docs[0].cover_i);
      const transformedBooks = data.docs.map((bookData) => {
        return {
          title: bookData.title,
          cover_i: bookData.cover_i,
        };
      });
      console.log(transformedBooks);
      getBooks(transformedBooks);
    } catch (error) {
      setError(error.message);
    }
    setLoading(false);
  };

  return (
    <div className="app">
      <Header
        searchTerm={searchTerm}
        getSearchTerm={getSearchTerm}
        fetchBooks={fetchBooks}
      ></Header>
      <div className="container">
        {loading && <div>Loading...</div>}
        {!loading && books.length > 0 && (
          <div className="container">
            {books.map((book, index) => {
              return (
                <Book
                  key={index}
                  title={book.title}
                  image={`https://covers.openlibrary.org/b/id/${book.cover_i}-M.jpg`}
                />
              );
            })}
          </div>
        )}
        {!loading && books.length === 0 && !error && <p>No books found</p>}
        {!loading && error && <p>{error}</p>}
      </div>
    </div>
  );
};

export default App;
