const App = () => {
  const [data, setData] = useState("nnn");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [images, setImages] = useState([]);

  useEffect(() => {
    setLoading(true);
    fetch("http://openlibrary.org/search.json?author=tolkien")
      .then((response) => response.json())
      .then((data) => setData(data))
      .then(() => setLoading(false))
      .catch(setError);

    console.log(data.docs);

    data.docs.forEach((element) => {
      fetch(` https://covers.openlibrary.org/b/isbn/${element.isbn}-M.jpg`)
        .then((response) => response.json())
        .then((images) => setImages(images));
      console.log(images);
    });
  }, []);

  if (loading) {
    return <h1>Loading...</h1>;
  }
  if (error) {
    return <pre>{JSON.stringify(error, null, 2)}</pre>;
  }

  if (data) {
    return (
      <div>
        {data.docs.map((element, index) => {
          <Book title={element.title} image={images[index]} />;
        })}
      </div>
    );
  }
};

export default App;
