import React, { useState, useEffect } from "react";

// const authorApi =
//   "https://openlibrary.org/search/authors.json?q=j%20k%20rowling";

// const searchApi = `https://openlibrary.org/search.json?title=${}`;

// console.log(data);

const App = () => {
  const [book, getBook] = useState();
  const [image, getImage] = useState();
  const [title, getTitle] = useState();

  const serachBooks = async (book) => {
    const res = await fetch(
      `https://openlibrary.org/search.json?title=${book}`
    );
    const data = await res.json();

    // getBooks(data.entries);
    console.log(data.docs[0].title);
    console.log(data.docs[0].isbn[2]);

    const imgresult = await fetch(
      `https://covers.openlibrary.org/b/isbn/${data.docs[0].isbn[2]}-M.jpg`
    );

    console.log(imgresult.url);
    getImage(imgresult.url);
    getTitle(data.docs[0].title);
  };
  console.log(image);
  useEffect(() => {
    serachBooks("tom sawyer");
  }, []);
  return (
    <div className="app">
      <h1>Book Land</h1>
      <div className="search">
        <input
          placeholder="Search for books"
          value=""
          onChange={() => {}}
        ></input>
        <img src="" alt="search" onClick={() => {}} />
      </div>
      <div className="container">
        <div className="book">
          <h3>{title}</h3>
          <img src={image} alt="search" />
        </div>
      </div>
    </div>
  );
};

export default App;
